import { useState, useEffect } from 'react';
import { ethers } from "ethers";
import { Row, Col, Card ,Form, Button} from 'react-bootstrap';
import { convertUriFormat } from './uriUtils.js';

export default function MyPurchases({ marketplace, nft, account }) {
  const [loading, setLoading] = useState(true);
  const [purchases, setPurchases] = useState([]);

  useEffect(() => {
    loadPurchasedItems();
  }, []);

  const loadPurchasedItems = async () => {
    try {
      const filter = marketplace.filters.Bought(null, null, null, null, null, account);
      const results = await marketplace.queryFilter(filter);
      //const [sellPrice, setSellPrice] = useState(null);

      const validPurchases = await Promise.all(
        results.map(async (i) => {
          try {
            const args = i.args;

            const newUri = await nft.tokenURI(args.tokenId);
            const uri = convertUriFormat(newUri);
            const response = await fetch(uri);
            const metadata = await response.json();

            const totalPrice = await marketplace.getTotalPrice(args.itemId);
            const imageUri = convertUriFormat(metadata.image);

            const purchasedItem = {
              totalPrice,
              price: args.price,
              itemId: args.itemId,
              name: metadata.name,
              description: metadata.description,
              image: imageUri,
            };

            return purchasedItem;
          } catch (error) {
            console.log("Error", error);
            return null;
          }
        })
      );

      const filteredPurchases = validPurchases.filter((item) => item !== null);

      setLoading(false);
      setPurchases(filteredPurchases);
    } catch (error) {
      console.log("Error loading purchased items:", error);
    }
  };

  const burnItem = async (purchasedItem) => {
    console.log(itemIdDecimal);

    const itemIdDecimal = purchasedItem.itemId.toNumber();
    console.log(itemIdDecimal);

    await (await nft.burn(itemIdDecimal)).wait();
    loadPurchasedItems();
  };


  /*const sellItem = async (purchasedItem) => {
    console.log("nft:", nft);
    console.log("itemId:", purchasedItem.itemId);
    console.log("sellPrice:", sellPrice);
    if (!nft || !purchasedItem.itemId || !sellPrice) return;

    try {
      await (await marketplace.offerTokenForSale(nft, purchasedItem.itemId, sellPrice)).wait();
    } catch (error) {
      console.log("You can't sell this token: ", error);
    }
    loadPurchasedItems();
  };*/

  useEffect(() => {
    loadPurchasedItems();
  }, []);

  if (loading) {
    return (
      <main style={{ padding: "1rem 0" }}>
        <h2>Loading...</h2>
      </main>
    );
  }

  return (
    <div className="flex justify-center">
      {purchases.length > 0 ? (
        <div className="px-5 container">
          <Row xs={1} md={2} lg={4} className="g-4 py-5">
            {purchases.map((item, idx) => (
              <Col key={idx} className="overflow-hidden">
                <Card>
                  <Card.Img variant="top" src={item.image} />
                  <Card.Footer>


                    <div className='d-grid' style={{ margin: '10px' }}>
                      <Button onClick={() => burnItem(item)} variant="danger" size="lg">
                        Burn 
                      </Button>
                    </div>

                  </Card.Footer>
                </Card>
              </Col>
            ))}
          </Row>
        </div>
      ) : (
        <main style={{ padding: "1rem 0" }}>
          <h2>No purchases</h2>
        </main>
      )}
    </div>
  );
}
